js:
var res = {};
var items = [];
var html = getResCode();

if (getResCode().indexOf('You') != -1) {
    fetch('http://www.135zy0.com' + html.split('\"')[3], {});
    items.push({
        title: '检测中，请过几秒再次下拉刷新，播放失败请切换至MX Player播放。',
        col_type: 'text_center_1'
    });
} else{
    for (var i = 1; i < html.split('xing_vb4').length; i++) {
        var title = parseDom(html, 'body&&.xing_vb&&li,' + i + '&&a&&Text').replace(/http.*html\//,'');
        var url = parseDom(html, '.xing_vb&&li,' + i + '&&a&&href');
        var type = parseDom(html, '.xing_vb&&li,' + i + '&&.xing_vb5&&Text').replace(/http.*html\//,'类型：');
        var desc = parseDom(html, '.xing_vb&&li,' + i + '&&span,-1&&Text').replace(/http.*html\//,'更新时间：');
        items.push({
            title: title,
            url: url,
            desc: type +" " + desc
        });
    }
}
res.data = items;
setHomeResult(res);

