js:
var res = {};
var items = [];
var html = getResCode();

var des_pic=parseDom(html,'body&&.vodImg&&img&&src');

var des_title=parseDom(parseDom(html,'body&&.vodinfobox&&Html').match(/<li>上映[\s\S]*?<\/li>/g)[0],'Text').replace(/http.*html\//,'');

var des_desc=parseDom(html,'body&&.vodplayinfo&&Text').replace(/http.*html\//,'');
//setError(des_title);

items.push({
			title: des_title,
    desc:des_desc,
    pic_url:des_pic,
    url:des_pic,
		col_type: 'pic_1'
});

//获取线路
var tabs = parseDom(html,"body&&.playBox,1&&Html").match(/<h3>[\s\S]*?<\/h3>/g);
//获取列表
var conts=parseDom(html,'body&&.playBox,1&&Html').match(/<ul>[\s]*?<li><input[\s\S]*?<\/ul>/g);
//setError(conts[0]);

//显示线路
for(var i = 0;i<conts.length;i++){
//类似fyIndex&&li
  var list=conts[i].match(/<li>[\s\S]*?<\/li>/g);
  var title =parseDom(tabs[i],"body&&Text").replace(getUrl()+'/','');
		items.push({
			//title: tabs[i].split(">")[2].split("<")[0],
     title: title,
			col_type: 'text_1'
		});
//显示选集
		for(var j = 0; j<list.length; j++){
			items.push({
				title:list[j].split('$')[0].split('>')[(list[j].split('$')[0].split('>').length-1)],
      url:list[j].split('$')[1].split('<')[0],
      col_type: 'text_2'
			});
}
}
//规则提供：香雅情
res.data = items;
setHomeResult(res);

