var des_desc=parseDom(html,'body&&.vodplayinfo&&Text').replace(/http.*html\//,'');
//setError(des_title);

items.push({
			title: des_title,
    desc:des_desc,
    pic_url:des_pic,
    url:des_pic,
		col_type: 'pic_1'
});

//获取线路
var tabs = parseDom(html,"body&&Html").match(/<h3>[\s\S]*?<\/h3> /g);
//获取列表
var conts=parseDom(html,'body&&.vodplayinfo,1&&Html').match(/<ul>[\s\S]*?<\/ul>/g);
//setError(conts.length);

//显示线路
for(var i = 0;i<conts.length;i++){
//类似fyIndex&&li
  var list=conts[i].split(/<li><\/li>/g);
  var title =parseDom(tabs[i],"body&&Text").replace(getUrl()+'/','');
		items.push({
			//title: tabs[i].split(">")[2].split("<")[0],
     title: title,
			col_type: 'text_1'
		});
//setError(list[0]);
//显示选集
		for(var j = 0; j<list.length-1; j++){
			items.push({
				title:list[j].split('$')[0].replace("<ul>\n",""),
url:"https://www.8090g.cn/jiexi/?url="+list[j].split('$')[1],
      //url:list[j].split('$')[1],
      col_type: 'text_2'
			});
}

}

//规则提供：香雅情
res.data = items;
setHomeResult(res);

